create table account (ACC_ID integer not null GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), ACC_LABEL varchar(32) not null unique, ACC_QUOTA bigint, ACC_CONSUMED_SMS bigint not null, primary key (ACC_ID))
create table application (APP_ID integer not null GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), APP_NAME varchar(32) not null unique, APP_CERTIFCATE varchar(4096) for bit data, APP_QUOTA bigint not null, APP_CONSUMED_SMS bigint not null, ACC_ID integer not null, INS_ID integer not null, primary key (APP_ID))
create table blacklist (BLA_ID integer not null GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), BLA_DATE timestamp not null, BLA_PHONE varchar(255) not null unique, APP_ID integer not null, primary key (BLA_ID))
create table institution (INS_ID integer not null GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), INS_LABEL varchar(32) not null unique, primary key (INS_ID))
create table sms (SMS_ID integer not null GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), SMS_INITIAL_ID integer, SMS_SENDER_ID integer, BROKER_SMS_ID varchar(255), SMS_STATE varchar(32) not null, SMS_DATE timestamp not null, SMS_ACK_DATE timestamp, SMS_PHONE varchar(255), APP_ID integer not null, ACC_ID integer not null, primary key (SMS_ID))
create table statistic (APP_ID integer not null, ACC_ID integer not null, STAT_MONTH date not null, STAT_NB_SMS bigint not null, STAT_NB_SMS_IN_ERROR bigint not null, primary key (APP_ID, ACC_ID, STAT_MONTH))
